css:
/*pc*/
@media (min-width: 601px){ 
 .header__nav {text-align: center;font-weight: 500; font-size:0.8rem;
   font-family: 'Roboto', sans-serif;}
.header__nav a:hover {background: transparent;color: #f02083;text-decoration: none;
  font-weight: 500;font-size: 0.8rem;
}
.header__nav a {text-transform: none;}
.menu__item:hover {text-decoration: none;
    background: #abd0dc;color: #e82b8b;font-family: 'Roboto', sans-serif;
  font-size: 0.8rem;}
.menu__item {text-decoration: none;
    background: #abd0dc;color:#3b4257;font-family: 'Roboto', sans-serif;
  font-size: 0.8rem;}
  .top-bar__page-links a {
   color: #82888c; font-weight: 300;font-family: 'Roboto', sans-serif; font-size: 0.8rem;
}
  .footer {
    background: var(--light);
    border-top: var(--spacer-1) solid #e7eff2;
    color: #394651;font-size: 1rem;
}
.footer__store {
    color: #3a4e64;font-family: 'Roboto', sans-serif;font-size:0.7rem;

}
  .footer__title {
    font-size: 1rem;font-family: 'Roboto', sans-serif;
  font-weight: 500;}
.footer {
    color: #394651;font-family: 'Roboto', sans-serif;font-size;1rem;
}
.ecom-credits {
    padding: 0.8rem;
  background: #f8f9fa;}
  .header__search-input {font-size: 1rem;font-family: 'Roboto', sans-serif;}
.page-title__head{font-size:1rem;font-family: 'Roboto', sans-serif;}
.product__buy{font-size:1.5rem;}
.share-network-whatsapp{font-size:0.7rem;}
  .product__name{font-family: 'Roboto', sans-serif;font-size:1rem;}
.prices__value{font-family: 'Roboto', sans-serif;font-size:1.6rem;}
.prices__discount{font-family: 'Roboto', sans-serif;font-size:1.3rem;}
. btn-lg{font-family: 'Roboto', sans-serif;font-size:1.2rem;}
.my-0{font-family: 'Roboto', sans-serif;font-size:1.1rem;color:#41524d;}
.prices--big .prices__value {font-size:1.2rem;font-family: 'Roboto', sans-serif;color:#3a4e64}
.product__sku{font-family: 'Roboto', sans-serif;font-size:0.8rem;}
.lead {font-size: 0.8rem; font-weight: 300;}
  h1{ font-size;1.2rem;font-family: 'Roboto', sans-serif;}
.products-carousel__title{font-size:1rem;}
}
.btn share {display:none;}
.ml-1 {
    font-size: 0rem;
}
.html-clearfix { border: 1px solid #ddd;
    padding: 0px 20px 0px 20px;}

}
/*responsivo*/ 
.header__logo {max-width: 300px;max-height: 100px;}
.header__buttons .btn{color: #3897d0;}
a {color: #375159;text-decoration: none;} 
.badge-primary {color: #fff;background-color: #f02083;}
.menu {background: #abd0dc;}
.menu__item {border-bottom: var(--border-width) solid #d2e3e3;}
.menu__link {text-align: center;
    display: block;
    border-top: var(--border-width) dashed #798fa5;
    margin: var(--spacer-3);
    padding: var(--spacer-3) var(--spacer-2);
    line-height: 1;
    color: #495e65;}
.menu__footer a {
    color: #273744;
    text-decoration: none;font-family: 'Roboto', sans-serif;
}
.btn-primary {
    color: #f7f7f7;
    background-color: #f02083;font-family: 'Roboto', sans-serif;
    border-color: #f02083;
}
.btn-primary a:hover {color: #ffffff;font-family: 'Roboto', sans-serif;
    background-color: #7e9cc3;
    border-color: #7e9cc3;
}

// Esconder botao de cancelamento de pedido
.order-info__toggle {
    display: none; !important;visibility: hidden;
}
.btn-danger {
    color: #fff;
    background-color: #fff;
    border-color: #fff;
}
.btn-danger:hover {
    color: #fff;
    background-color: #fff;
    border-color: #fff;
}.btn{font-size:1rem;}
} 
/*mobile*/

@media(max-width: 480px) {
  #page-home .banners-grid .container .col-12 {
  max-width: 33.333%;
  flex: 0 0 33.333%
  }
}
